import * as React from 'react';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useSelector } from 'react-redux';

export default function RestaurantListPage() {
  const [restaurants, setRestaurants] = React.useState([]);
  const [restaurateurRestaurants, setRestaurateurRestaurants] = React.useState([]);
  const [searchTerm, setSearchTerm] = React.useState('');
  const [category, setCategory] = React.useState('');
  const { userId, role } = useSelector((state) => state.user);

  React.useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        let url = 'http://localhost:8080/api/restaurants';

        if (role === 'restaurateur') {
          url = `http://localhost:8080/api/restaurants/restaurateur/${userId}`;
        } else {
          const params = new URLSearchParams(window.location.search);
          const latitude = params.get('latitude');
          const longitude = params.get('longitude');
          url = `http://localhost:8080/api/restaurants/location?latitude=${latitude}&longitude=${longitude}`;
        }

        console.log('Fetching URL:', url);
        const response = await fetch(url);
        const data = await response.json();
        
        console.log('Fetched data:', data);
        if (Array.isArray(data)) {
          setRestaurants(data);
        } else {
          console.error('Data is not an array:', data);
        }
      } catch (error) {
        console.error('Error fetching restaurants:', error);
      }
    };

    const fetchRestaurateurRestaurants = async () => {
      if (role === 'restaurateur') {
        try {
          const response = await fetch(`http://localhost:8080/api/restaurants/restaurateur/${userId}`);
          const data = await response.json();
          
          console.log('Fetched restaurateur data:', data);
          if (Array.isArray(data)) {
            setRestaurateurRestaurants(data);
          } else {
            console.error('Restaurateur data is not an array:', data);
          }
        } catch (error) {
          console.error('Error fetching restaurateur restaurants:', error);
        }
      }
    };

    if (userId && role) {
      console.log('User ID:', userId);
      console.log('Role:', role);
      fetchRestaurants();
      fetchRestaurateurRestaurants();
    } else {
      console.error('User ID or role is not defined');
    }
  }, [role, userId]);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleCategoryClick = (category) => {
    setCategory(category);
  };

  const filteredRestaurants = restaurants.filter(restaurant => 
    restaurant.nom_Restaurant && restaurant.nom_Restaurant.toLowerCase().includes(searchTerm.toLowerCase()) &&
    (category ? restaurant.category === category : true)
  );

  const filteredRestaurateurRestaurants = restaurateurRestaurants.filter(restaurant => 
    restaurant.nom_Restaurant && restaurant.nom_Restaurant.toLowerCase().includes(searchTerm.toLowerCase()) &&
    (category ? restaurant.category === category : true)
  );

  return (
    <Container>
      <Box sx={{ mt: 12 }}> {/* Ajustement de la marge supérieure pour éviter le chevauchement avec AppBar */}
        <Typography variant="h4" gutterBottom>
          Restaurants disponibles
        </Typography>
        <Box sx={{ display: 'flex', mb: 2 }}>
          <TextField
            label="Recherche par nom"
            variant="outlined"
            fullWidth
            value={searchTerm}
            onChange={handleSearchChange}
            sx={{ mr: 2 }}
          />
          <Button variant="contained" color="primary" onClick={() => handleCategoryClick('')}>
            Réinitialiser les filtres
          </Button>
        </Box>
        <Box sx={{ display: 'flex', gap: 2, mb: 4 }}>
          <Button variant="outlined" onClick={() => handleCategoryClick('Italian')}>
            Italien
          </Button>
          <Button variant="outlined" onClick={() => handleCategoryClick('Chinese')}>
            Chinois
          </Button>
          <Button variant="outlined" onClick={() => handleCategoryClick('Mexican')}>
            Mexicain
          </Button>
          {/* Ajoutez plus de catégories ici */}
        </Box>
        
        {role === 'restaurateur' && restaurateurRestaurants.length > 0 && (
          <Box sx={{ mt: 4 }}>
            <Typography variant="h5" gutterBottom>
              Mes Restaurants
            </Typography>
            <Grid container spacing={4}>
              {filteredRestaurateurRestaurants.map((restaurant) => (
                <Grid item key={restaurant.ID_Restaurant} xs={12} sm={6} md={4}>
                  <Card>
                    {restaurant.image && (
                      <CardMedia
                        component="img"
                        height="140"
                        image={`http://localhost:3001/${restaurant.image}`}
                        alt={restaurant.nom_Restaurant}
                      />
                    )}
                    <CardContent>
                      <Typography gutterBottom variant="h5" component="div">
                        {restaurant.nom_Restaurant || 'Nom non disponible'}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {restaurant.adresse || 'Adresse non disponible'}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {restaurant.category || 'Catégorie non disponible'}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>
        )}
        
        <Typography variant="h5" gutterBottom>
          Tous les Restaurants
        </Typography>
        <Grid container spacing={4}>
          {filteredRestaurants.map((restaurant) => (
            <Grid item key={restaurant.ID_Restaurant} xs={12} sm={6} md={4}>
              <Card>
                {restaurant.image && (
                  <CardMedia
                    component="img"
                    height="140"
                    image={`http://localhost:3001/${restaurant.image}`}
                    alt={restaurant.nom_Restaurant}
                  />
                )}
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {restaurant.nom_Restaurant || 'Nom non disponible'}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {restaurant.adresse || 'Adresse non disponible'}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {restaurant.category || 'Catégorie non disponible'}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
}
