import * as React from 'react';
import { alpha } from '@mui/material';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Link from '@mui/material/Link';
import Stack from '@mui/material/Stack';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import { useNavigate } from 'react-router-dom';
import Autocomplete from '@mui/material/Autocomplete';
import { loadScript } from "@react-google-maps/api";

const libraries = ['places'];

export default function Hero() {
  const [address, setAddress] = React.useState('');
  const [options, setOptions] = React.useState([]);
  const navigate = useNavigate();

  React.useEffect(() => {
    // Charger le script Google Maps Places
    loadScript({
      googleMapsApiKey: "YOUR_GOOGLE_MAPS_API_KEY",
      libraries,
    }).then(() => {
      console.log("Google Maps script loaded");
    }).catch((e) => {
      console.error("Error loading Google Maps script:", e);
    });
  }, []);

  const handleSearch = async () => {
    try {
      const response = await fetch(`https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(address)}&key=YOUR_GOOGLE_MAPS_API_KEY`);
      const data = await response.json();

      if (data.results && data.results.length > 0) {
        const { lat, lng } = data.results[0].geometry.location;
        navigate(`/restaurants?latitude=${lat}&longitude=${lng}`);
      } else {
        console.error('No results found for the given address');
      }
    } catch (error) {
      console.error('Error fetching geocode data:', error);
    }
  };

  const handleAddressChange = (event, newValue) => {
    setAddress(newValue);
    if (newValue) {
      const service = new window.google.maps.places.AutocompleteService();
      service.getPlacePredictions({ input: newValue, componentRestrictions: { country: 'fr' } }, (predictions, status) => {
        if (status === window.google.maps.places.PlacesServiceStatus.OK && predictions) {
          setOptions(predictions.map(prediction => prediction.description));
        }
      });
    } else {
      setOptions([]);
    }
  };

  return (
    <Box
      id="hero"
      sx={(theme) => ({
        width: '100%',
        backgroundImage:
          theme.palette.mode === 'light'
            ? 'linear-gradient(180deg, #CEE5FD, #FFF)'
            : `linear-gradient(#02294F, ${alpha('#090E10', 0.0)})`,
        backgroundSize: '100% 20%',
        backgroundRepeat: 'no-repeat',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center',
      })}
    >
      <Container
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          pt: { xs: 14, sm: 20 },
          pb: { xs: 8, sm: 12 },
        }}
      >
        <Stack spacing={2} useFlexGap sx={{ width: { xs: '100%', sm: '70%' }, textAlign: 'center' }}>
          <Typography
            variant="h1"
            sx={{
              textAlign: 'center',
              fontSize: 'clamp(3.5rem, 10vw, 4rem)',
            }}
          >
            Découvrez les&nbsp;
            <Typography
              component="span"
              variant="h1"
              sx={{
                fontSize: 'clamp(3rem, 10vw, 4rem)',
                color: (theme) =>
                  theme.palette.mode === 'light' ? 'primary.main' : 'primary.light',
              }}
            >
              meilleurs restaurants!
            </Typography>
          </Typography>
          <Typography
            textAlign="center"
            color="text.secondary"
            sx={{ width: { sm: '100%', md: '80%' }, margin: '0 auto' }}
          >
            Entrez votre adresse pour une aventure culinaire incroyable! Nous vous connectons aux meilleurs restaurants locaux.
          </Typography>
          <Stack
            direction={{ xs: 'column', sm: 'row' }}
            alignSelf="center"
            spacing={1}
            useFlexGap
            sx={{ pt: 2, width: { xs: '100%', sm: 'auto' } }}
          >
            <Autocomplete
              freeSolo
              options={options}
              value={address}
              onInputChange={handleAddressChange}
              renderInput={(params) => (
                <TextField
                  {...params}
                  id="outlined-basic"
                  hiddenLabel
                  size="small"
                  variant="outlined"
                  aria-label="Entrez votre adresse"
                  placeholder="Votre adresse"
                  inputProps={{
                    ...params.inputProps,
                    autoComplete: 'off',
                  }}
                />
              )}
            />
            <Button variant="contained" color="primary" onClick={handleSearch}>
              Trouvez des restaurants
            </Button>
          </Stack>
          <Typography variant="caption" textAlign="center" sx={{ opacity: 0.8 }}>
            En cliquant sur "Trouvez des restaurants" vous acceptez nos&nbsp;
            <Link href="#" color="primary">
              Termes et Conditions
            </Link>
            .
          </Typography>
        </Stack>
        <Box
          id="image"
          sx={(theme) => ({
            mt: { xs: 8, sm: 10 },
            alignSelf: 'center',
            height: { xs: 200, sm: 700 },
            width: '100%',
            backgroundImage: `url(${require('../../assets/image/background.webp')})`,
            backgroundSize: 'cover',
            borderRadius: '10px',
            outline: '1px solid',
            outlineColor:
              theme.palette.mode === 'light'
                ? alpha('#BFCCD9', 0.5)
                : alpha('#9CCCFC', 0.1),
            boxShadow:
              theme.palette.mode === 'light'
                ? `0 0 12px 8px ${alpha('#9CCCFC', 0.2)}`
                : `0 0 24px 12px ${alpha('#033363', 0.2)}`,
          })}
        />
      </Container>
    </Box>
  );
}
