import React, { useState } from 'react';
import { alpha } from '@mui/material';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Link from '@mui/material/Link';
import Stack from '@mui/material/Stack';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Autocomplete from '@mui/material/Autocomplete';

export default function Hero() {
  const [address, setAddress] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  const fetchSuggestions = async (query) => {
    try {
      const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${query}&addressdetails=1&limit=5`);
      const data = await response.json();
      setSuggestions(data.map((result) => ({
        label: result.display_name,
        lat: result.lat,
        lon: result.lon,
      })));
    } catch (error) {
      console.error('Error fetching address suggestions:', error);
    }
  };

  const handleInputChange = (event, value) => {
    setAddress(value);
    if (value.length > 3) {
      fetchSuggestions(value);
    } else {
      setSuggestions([]);
    }
  };

  const handleSearch = async () => {
    if (address) {
      try {
        const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${address}`);
        const data = await response.json();
        if (data.length > 0) {
          const { lat, lon } = data[0];
          // Faites quelque chose avec la latitude et la longitude, par exemple, rediriger vers la page des restaurants
          console.log(`Latitude: ${lat}, Longitude: ${lon}`);
        } else {
          console.error('No results found for the address');
        }
      } catch (error) {
        console.error('Error fetching geocode data:', error);
      }
    }
  };

  return (
    <Box
      id="hero"
      sx={(theme) => ({
        width: '100%',
        backgroundImage:
          theme.palette.mode === 'light'
            ? 'linear-gradient(180deg, #CEE5FD, #FFF)'
            : `linear-gradient(#02294F, ${alpha('#090E10', 0.0)})`,
        backgroundSize: '100% 20%',
        backgroundRepeat: 'no-repeat',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center',
      })}
    >
      <Container
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          pt: { xs: 14, sm: 20 },
          pb: { xs: 8, sm: 12 },
        }}
      >
        <Stack spacing={2} useFlexGap sx={{ width: { xs: '100%', sm: '70%' }, textAlign: 'center' }}>
          <Typography
            variant="h1"
            sx={{
              textAlign: 'center',
              fontSize: 'clamp(3.5rem, 10vw, 4rem)',
            }}
          >
            DÃ©couvrez les&nbsp;
            <Typography
              component="span"
              variant="h1"
              sx={{
                fontSize: 'clamp(3rem, 10vw, 4rem)',
                color: (theme) =>
                  theme.palette.mode === 'light' ? 'primary.main' : 'primary.light',
              }}
            >
              meilleurs restaurants!
            </Typography>
          </Typography>
          <Typography
            textAlign="center"
            color="text.secondary"
            sx={{ width: { sm: '100%', md: '80%' }, margin: '0 auto' }}
          >
            Entrez votre adresse pour une aventure culinaire incroyable! Nous vous connectons aux meilleurs restaurants locaux.
          </Typography>
          <Stack
            direction={{ xs: 'column', sm: 'row' }}
            alignSelf="center"
            spacing={1}
            useFlexGap
            sx={{ pt: 2, width: { xs: '100%', sm: 'auto' } }}
          >
            <Autocomplete
              freeSolo
              options={suggestions}
              getOptionLabel={(option) => option.label}
              onInputChange={handleInputChange}
              renderInput={(params) => (
                <TextField
                  {...params}
                  id="outlined-basic"
                  hiddenLabel
                  size="medium"
                  variant="outlined"
                  aria-label="Entrez votre adresse"
                  placeholder="Votre adresse"
                  sx={{
                    '& .MuiInputBase-root': {
                      height: '56px',
                      fontSize: '1.2rem',
                      paddingLeft: '16px',
                    },
                    '& .MuiOutlinedInput-root': {
                      borderRadius: '10px',
                    },
                    width: '400px', // Ajustez cette valeur selon vos besoins
                  }}
                  inputProps={{
                    ...params.inputProps,
                    autoComplete: 'off',
                    'aria-label': 'Entrez votre adresse',
                  }}
                />
              )}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={handleSearch}
              sx={{
                height: '56px',
                fontSize: '1.2rem',
                borderRadius: '10px',
                padding: '0 24px',
              }}
            >
              Trouvez des restaurants
            </Button>
          </Stack>
          <Typography variant="caption" textAlign="center" sx={{ opacity: 0.8 }}>
            En cliquant sur "Trouvez des restaurants" vous acceptez nos&nbsp;
            <Link href="#" color="primary">
              Termes et Conditions
            </Link>
            .
          </Typography>
        </Stack>
        <Box
          id="image"
          sx={(theme) => ({
            mt: { xs: 8, sm: 10 },
            alignSelf: 'center',
            height: { xs: 200, sm: 700 },
            width: '100%',
            backgroundImage: `url(${require('../../assets/image/background.webp')})`,
            backgroundSize: 'cover',
            borderRadius: '10px',
            outline: '1px solid',
            outlineColor:
              theme.palette.mode === 'light'
                ? alpha('#BFCCD9', 0.5)
                : alpha('#9CCCFC', 0.1),
            boxShadow:
              theme.palette.mode === 'light'
                ? `0 0 12px 8px ${alpha('#9CCCFC', 0.2)}`
                : `0 0 24px 12px ${alpha('#033363', 0.2)}`,
          })}
        />
      </Container>
    </Box>
  );
}
