import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import {
  Container,
  Box,
  Grid,
  TextField,
  Button,
  Typography,
  Paper,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Avatar,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import axios from 'axios';  // Assurez-vous d'importer axios

export default function RestaurantManagement() {
  const { user, role } = useSelector((state) => state.user);
  const [articles, setArticles] = useState([]);
  const [menus, setMenus] = useState([]);
  const [articleName, setArticleName] = useState('');
  const [articlePrice, setArticlePrice] = useState('');
  const [menuName, setMenuName] = useState('');
  const [selectedArticles, setSelectedArticles] = useState([]);
  const [openArticleDialog, setOpenArticleDialog] = useState(false);
  const [openMenuDialog, setOpenMenuDialog] = useState(false);

  // Fonction pour récupérer les articles depuis l'API
  const fetchArticles = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/articles');
      setArticles(response.data);
    } catch (error) {
      console.error('Error fetching articles:', error);
    }
  };

  // Fonction pour récupérer les menus depuis l'API
  const fetchMenus = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/menus');
      setMenus(response.data);
    } catch (error) {
      console.error('Error fetching menus:', error);
    }
  };

  useEffect(() => {
    fetchArticles();
    fetchMenus();
  }, []);

  const handleAddArticle = () => {
    const newArticle = { name: articleName, price: articlePrice };
    setArticles([...articles, newArticle]);
    setArticleName('');
    setArticlePrice('');
    setOpenArticleDialog(false);
  };

  const handleAddMenu = () => {
    const newMenu = { name: menuName, articles: selectedArticles };
    setMenus([...menus, newMenu]);
    setMenuName('');
    setSelectedArticles([]);
    setOpenMenuDialog(false);
  };

  const handleDeleteArticle = (index) => {
    const newArticles = [...articles];
    newArticles.splice(index, 1);
    setArticles(newArticles);
  };

  const handleDeleteMenu = (index) => {
    const newMenus = [...menus];
    newMenus.splice(index, 1);
    setMenus(newMenus);
  };

  const handleArticleSelect = (article) => {
    if (!selectedArticles.includes(article)) {
      setSelectedArticles([...selectedArticles, article]);
    }
  };

  return (
    <Container component="main" maxWidth="md">
      <Box sx={{ marginTop: 12, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Avatar sx={{ width: 100, height: 100, mb: 2 }} />
        <Typography component="h1" variant="h5">
          {user.username}
        </Typography>
        <Typography variant="body1" color="textSecondary">
          {role}
        </Typography>
        <Box sx={{ mt: 3, width: '100%' }}>
          <Grid container spacing={3}>
            <Grid item xs={12} sm={6}>
              <Paper sx={{ p: 2 }}>
                <Typography component="h2" variant="h6">
                  Articles
                </Typography>
                <Button
                  variant="contained"
                  color="primary"
                  startIcon={<AddIcon />}
                  onClick={() => setOpenArticleDialog(true)}
                  sx={{ mt: 2 }}
                >
                  Ajouter un Article
                </Button>
                <List>
                  {articles.map((article, index) => (
                    <ListItem key={index}>
                      <ListItemText primary={article.nom_Article} secondary={`Prix: ${article.prix}€`} />
                      <IconButton edge="end" aria-label="delete" onClick={() => handleDeleteArticle(index)}>
                        <DeleteIcon />
                      </IconButton>
                    </ListItem>
                  ))}
                </List>
              </Paper>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Paper sx={{ p: 2 }}>
                <Typography component="h2" variant="h6">
                  Menus
                </Typography>
                <Button
                  variant="contained"
                  color="primary"
                  startIcon={<MenuBookIcon />}
                  onClick={() => setOpenMenuDialog(true)}
                  sx={{ mt: 2 }}
                >
                  Créer un Menu
                </Button>
                <List>
                  {menus.map((menu, index) => (
                    <ListItem key={index}>
                      <ListItemText
                        primary={menu.nom_Menu}
                        secondary={`Articles: ${menu.articles.map((article) => article.nom_Article).join(', ')}`}
                      />
                      <IconButton edge="end" aria-label="delete" onClick={() => handleDeleteMenu(index)}>
                        <DeleteIcon />
                      </IconButton>
                    </ListItem>
                  ))}
                </List>
              </Paper>
            </Grid>
          </Grid>
        </Box>
      </Box>

      <Dialog open={openArticleDialog} onClose={() => setOpenArticleDialog(false)}>
        <DialogTitle>Ajouter un Article</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Nom de l'Article"
            fullWidth
            variant="outlined"
            value={articleName}
            onChange={(e) => setArticleName(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Prix de l'Article"
            fullWidth
            variant="outlined"
            type="number"
            value={articlePrice}
            onChange={(e) => setArticlePrice(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenArticleDialog(false)} color="primary">
            Annuler
          </Button>
          <Button onClick={handleAddArticle} color="primary">
            Ajouter
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog open={openMenuDialog} onClose={() => setOpenMenuDialog(false)}>
        <DialogTitle>Créer un Menu</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Nom du Menu"
            fullWidth
            variant="outlined"
            value={menuName}
            onChange={(e) => setMenuName(e.target.value)}
          />
          <Typography variant="h6" sx={{ mt: 2 }}>
            Sélectionner les Articles
          </Typography>
          <List>
            {articles.map((article, index) => (
              <ListItem key={index} button onClick={() => handleArticleSelect(article)}>
                <ListItemText primary={article.nom_Article} secondary={`Prix: ${article.prix}€`} />
              </ListItem>
            ))}
          </List>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenMenuDialog(false)} color="primary">
            Annuler
          </Button>
          <Button onClick={handleAddMenu} color="primary">
            Créer
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}
