// src/theme.js

import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      light: '#9CCCFC',
      main: '#0959AA',
      dark: '#02294F',
      contrastText: '#FFFFFF',
    },
    secondary: {
      light: '#D49CFC',
      main: '#6709AA',
      dark: '#2F024F',
      contrastText: '#FFFFFF',
    },
    background: {
      default: '#FFFFFF',
      paper: '#F0F7FF',
    },
    text: {
      primary: '#090E10',
      secondary: '#4C5967',
    },
  },
  typography: {
    fontFamily: '"Inter", "sans-serif"',
    h1: {
      fontSize: '60px',
      fontWeight: 600,
      lineHeight: 1.114,
      letterSpacing: -0.2,
      color: '#0959AA',
    },
    h2: {
      fontSize: '48px',
      fontWeight: 600,
      lineHeight: 1.2,
      color: '#0959AA',
    },
    h3: {
      fontSize: '42px',
      lineHeight: 1.2,
      color: '#0959AA',
    },
    h4: {
      fontSize: '36px',
      fontWeight: 500,
      lineHeight: 1.5,
      color: '#0959AA',
    },
    h5: {
      fontSize: '20px',
      fontWeight: 600,
      color: '#0959AA',
    },
    h6: {
      fontSize: '18px',
      color: '#0959AA',
    },
    subtitle1: {
      fontSize: '18px',
      color: '#4C5967',
    },
    subtitle2: {
      fontSize: '16px',
      color: '#4C5967',
    },
    body1: {
      fontWeight: 400,
      fontSize: '15px',
      color: '#090E10',
    },
    body2: {
      fontWeight: 400,
      fontSize: '14px',
      color: '#090E10',
    },
    caption: {
      fontWeight: 400,
      fontSize: '12px',
      color: '#4C5967',
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none', // Pour désactiver la transformation du texte en majuscules
        },
        contained: {
          backgroundColor: '#0959AA',
          '&:hover': {
            backgroundColor: '#064079',
          },
        },
        outlined: {
          borderColor: '#0959AA',
          color: '#0959AA',
          '&:hover': {
            backgroundColor: 'rgba(9, 89, 170, 0.04)',
            borderColor: '#064079',
          },
        },
        text: {
          color: '#0959AA',
          '&:hover': {
            backgroundColor: 'rgba(9, 89, 170, 0.04)',
          },
        },
      },
    },
  },
});

export default theme;
