import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      light: '#FFB74D',
      main: '#FF9800',
      dark: '#F57C00',
      contrastText: '#FFFFFF',
    },
    secondary: {
      light: '#D49CFC',
      main: '#6709AA',
      dark: '#2F024F',
      contrastText: '#FFFFFF',
    },
    background: {
      default: '#FFFFFF',
      paper: '#F0F7FF',
    },
    text: {
      primary: '#090E10',
      secondary: '#4C5967',
    },
  },
  typography: {
    fontFamily: '"Poppins", "sans-serif"', // Changez la police ici
    h1: {
      fontSize: 60,
      fontWeight: 600,
      lineHeight: 78 / 70,
      letterSpacing: -0.2,
    },
    h2: {
      fontSize: 48,
      fontWeight: 600,
      lineHeight: 1.2,
    },
    h3: {
      fontSize: 42,
      lineHeight: 1.2,
    },
    h4: {
      fontSize: 36,
      fontWeight: 500,
      lineHeight: 1.5,
    },
    h5: {
      fontSize: 20,
      fontWeight: 600,
    },
    h6: {
      fontSize: 18,
    },
    subtitle1: {
      fontSize: 18,
    },
    subtitle2: {
      fontSize: 16,
    },
    body1: {
      fontWeight: 400,
      fontSize: 15,
    },
    body2: {
      fontWeight: 400,
      fontSize: 14,
    },
    caption: {
      fontWeight: 400,
      fontSize: 12,
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none', // Pour désactiver la transformation du texte en majuscules
        },
        contained: {
          backgroundColor: '#FF9800',
          '&:hover': {
            backgroundColor: '#F57C00',
          },
        },
        outlined: {
          borderColor: '#FF9800',
          color: '#FF9800',
          '&:hover': {
            backgroundColor: 'rgba(255, 152, 0, 0.04)',
            borderColor: '#F57C00',
          },
        },
        text: {
          color: '#FF9800',
          '&:hover': {
            backgroundColor: 'rgba(255, 152, 0, 0.04)',
          },
        },
      },
    },
  },
});

export default theme;
