import React, { useState, useEffect } from 'react';
import { cartApi } from '../api/api';

function OrderPage() {
  const [cart, setCart] = useState(null);
  const [clientId] = useState(1); // Remplacer par l'ID du client réel

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await cartApi.get(`/carts/${clientId}`);
        setCart(response.data);
      } catch (error) {
        console.error('Error fetching cart:', error);
      }
    };

    fetchCart();
  }, [clientId]);

  if (!cart) {
    return <p>Loading cart...</p>;
  }

  return (
    <div>
      <h1>Votre Panier</h1>
      {cart.articles.length > 0 ? (
        <ul>
          {cart.articles.map((articleId, index) => (
            <li key={index}>
              <p>ID Article: {articleId}</p>
              {/* Afficher plus de détails sur l'article si nécessaire */}
            </li>
          ))}
        </ul>
      ) : (
        <p>Votre panier est vide.</p>
      )}
      <p>Total Price: {cart.price}</p>
    </div>
  );
}

export default OrderPage;
