import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

function AnimatedMarker({ start, end, steps, interval }) {
  const [position, setPosition] = useState(start);

  useEffect(() => {
    let lat = start[0];
    let lng = start[1];
    let step = 0;
    const latStep = (end[0] - start[0]) / steps;
    const lngStep = (end[1] - start[1]) / steps;

    const intervalId = setInterval(() => {
      lat += latStep;
      lng += lngStep;
      setPosition([lat, lng]);
      step += 1;
      if (step >= steps) {
        clearInterval(intervalId);
        alert('Le livreur est arrivé à destination!');
      }
    }, interval); // interval time in milliseconds

    return () => clearInterval(intervalId);
  }, [start, end, steps, interval]);

  return (
    <Marker position={position}>
      <Popup>Delivery in progress</Popup>
    </Marker>
  );
}

function DeliveryTrackingPage() {
  const start = [48.55445700, 7.68174900];
  const end = [48.8566, 2.3522];

  // Adjust these values to change the speed
  const steps = 100; // Increase for slower, decrease for faster
  const interval = 50; // Decrease for faster, increase for slower

  let DefaultIcon = L.icon({
    iconUrl: "https://unpkg.com/leaflet@1.6/dist/images/marker-icon.png",
  });
  L.Marker.prototype.options.icon = DefaultIcon;

  return (
    <div>
      <h1>Delivery Tracking</h1>
      <MapContainer
        center={start}
        zoom={13}
        scrollWheelZoom={true} // Enable scroll wheel zoom
        style={{ height: "600px", width: "100%" }}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <AnimatedMarker start={start} end={end} steps={steps} interval={interval} />
        <Marker position={end}>
          <Popup>Destination</Popup>
        </Marker>
      </MapContainer>
    </div>
  );
}

export default DeliveryTrackingPage;
