import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';
import 'leaflet-routing-machine';

function DeliveryTrackingPage() {
  const [markerPosition, setMarkerPosition] = useState([48.55445700, 7.68174900]);
  const position2 = [48.6, 7.68174900];

  const mapRef = useRef(null);

  useEffect(() => {
    const map = mapRef.current;

    if (!map) return;

    const waypoints = [
      L.latLng(markerPosition),
      L.latLng(position2)
    ];

    const routingControl = L.Routing.control({
      waypoints: waypoints,
      createMarker: function() { return null; },
      lineOptions: {
        styles: [{ color: 'blue', opacity: 0.6, weight: 4 }]
      }
    }).addTo(map);

    let currentIndex = 0;
    const updateMarkerPosition = () => {
      if (currentIndex < waypoints.length - 1) {
        currentIndex++;
        setMarkerPosition([waypoints[currentIndex].lat, waypoints[currentIndex].lng]);
        setTimeout(updateMarkerPosition, 1000); // Adjust the speed here
      }
    };

    setTimeout(updateMarkerPosition, 10000);

    return () => map.removeControl(routingControl);
  }, [mapRef]);

  let DefaultIcon = L.icon({
    iconUrl: "https://unpkg.com/leaflet@1.6/dist/images/marker-icon.png",
  });
  L.Marker.prototype.options.icon = DefaultIcon;

  return (
    <div>
      <h1>Delivery Tracking</h1>
      <MapContainer
        center={markerPosition}
        zoom={13}
        scrollWheelZoom={false}
        style={{ height: "600px", width: "100%" }}
        ref={mapRef}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={markerPosition}>
          <Popup>
            Livre en cours de livraison
          </Popup>
        </Marker>
        <Marker position={position2}>
          <Popup>
            Destination
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
}

export default DeliveryTrackingPage;
